INSERT INTO `deceases_symptom` VALUES (2992,'Приступы головокружения (предметы вращаются вокруг человека). Им предшествует ощущение заложенности, полноты в ухе в течение нескольких дней. Приступы по частоте: очень частые (несколько раз в неделю); частые (1-2 раз в месяц); редкие (1-2 раза в год); эпизодические (1 раз в несколько лет). Продолжительность приступа — от минуты до суток, чаще — от 2 до 8 часов. После приступа в течение 5-48 часов пациенты отмечают слабость, снижение работоспособности, после чего состояние нормализуется, больные чувствуют себя здоровыми до нового приступа.','Приступы головокружения (предметы вращаются вокруг человека). Им предшествует ощущение заложенности, полноты в ухе в течение нескольких дней. Приступы по частоте: очень частые (несколько раз в неделю); частые (1-2 раз в месяц); редкие (1-2 раза в год); эпизодические (1 раз в несколько лет). Продолжительность приступа — от минуты до суток, чаще — от 2 до 8 часов. После приступа в течение 5-48 часов пациенты отмечают слабость, снижение работоспособности, после чего состояние нормализуется, больные чувствуют себя здоровыми до нового приступа.',1,2,1303,0,NULL,NULL);
