INSERT INTO `deceases_symptom` VALUES (4169,'Афферентная моторная афазия — делает сложным поиск отдельной артикуляторной позы, необходимой для произнесения конкретного звука, нарушается понимание разговорной и письменной речи.','Афферентная моторная афазия — делает сложным поиск отдельной артикуляторной позы, необходимой для произнесения конкретного звука, нарушается понимание разговорной и письменной речи.',1,2,42,0,NULL,NULL);
INSERT INTO `deceases_symptom` VALUES (4170,'Акустико-гностическая афазия (сенсорная афазия) — невозможность понять обращенную речь при сохранном слухе, «речевая окрошка», логорея, нарушения письма, чтение сохранно.','Акустико-гностическая афазия (сенсорная афазия) — невозможность понять обращенную речь при сохранном слухе, «речевая окрошка», логорея, нарушения письма, чтение сохранно.',1,2,23,0,NULL,NULL);
INSERT INTO `deceases_symptom` VALUES (4171,'Акустико-мнестическая афазия — страдает слуховая и речевая память, визуальное представление о предмете, трудно удерживается информация, воспринимаемая слухом, речевая активность повышена.','Акустико-мнестическая афазия — страдает слуховая и речевая память, визуальное представление о предмете, трудно удерживается информация, воспринимаемая слухом, речевая активность повышена.',1,2,24,0,NULL,NULL);
INSERT INTO `deceases_symptom` VALUES (4172,'Оптико-мнестическая афазия — страдают зрительная память, понимание читаемого текста, письменные навыки, операции счета.','Оптико-мнестическая афазия — страдают зрительная память, понимание читаемого текста, письменные навыки, операции счета.',1,2,925,0,NULL,NULL);
INSERT INTO `deceases_symptom` VALUES (4173,'Амнестико-семантическая афазия — забываются названия предметов и явлений, общеупотребительных фраз, возникают трудности с пониманием читаемого текста.','Амнестико-семантическая афазия — забываются названия предметов и явлений, общеупотребительных фраз, возникают трудности с пониманием читаемого текста.',1,2,28,0,NULL,NULL);
