INSERT INTO `deceases_symptom` VALUES (4180,'характеризуется тем, что больной теряет способность запоминать и фиксировать в сознании текущие и произошедшие в недавнем прошлом события. Все, что в настоящий момент имело место быть, сразу же забывается. Данный тип нарушений патогномоничен для синдрома Корсакова при алкогольном полиневритическом психозе.','характеризуется тем, что больной теряет способность запоминать и фиксировать в сознании текущие и произошедшие в недавнем прошлом события. Все, что в настоящий момент имело место быть, сразу же забывается. Данный тип нарушений патогномоничен для синдрома Корсакова при алкогольном полиневритическом психозе.',1,2,2321,0,NULL,NULL);
